import { Effect } from './effector';
/**
 * DefaultEffectorStream is the default implementation of EffectorStream.
 */
export class DefaultEffectorStream {
    constructor(expr) {
        this.done = false;
        this.res = false;
        this.rec = false;
        this.expr = expr;
    }
    current() {
        return this.res;
    }
    pushEffect(eft) {
        switch (this.expr) {
            case "some(where (p_eft == allow))" /* ALLOW */:
                if (eft === Effect.Allow) {
                    this.res = true;
                    this.done = true;
                    this.rec = true;
                }
                break;
            case "!some(where (p_eft == deny))" /* DENY */:
                this.res = true;
                if (eft === Effect.Deny) {
                    this.res = false;
                    this.done = true;
                    this.rec = true;
                }
                break;
            case "some(where (p_eft == allow)) && !some(where (p_eft == deny))" /* ALLOW_AND_DENY */:
                if (eft === Effect.Allow) {
                    this.res = true;
                    this.rec = true;
                }
                else if (eft === Effect.Deny) {
                    this.res = false;
                    this.done = true;
                    this.rec = true;
                }
                else {
                    this.rec = false;
                }
                break;
            case "priority(p_eft) || deny" /* PRIORITY */:
                if (eft !== Effect.Indeterminate) {
                    this.res = eft === Effect.Allow;
                    this.done = true;
                    this.rec = true;
                }
                break;
            default:
                throw new Error('unsupported effect');
        }
        return [this.res, this.rec, this.done];
    }
}
